const buttonItem=document.querySelectorAll(".filter button"),productItem=document.querySelectorAll(".products .product");buttonItem.forEach(t=>{t.onclick=function(){buttonItem.forEach(t=>{t.classList.remove("active")}),t.classList.add("active");let e=t.textContent.toLowerCase();productItem.forEach(t=>{t.style.display="none";t.getAttribute("data-filter").split(" ").forEach(o=>{o===e&&(t.style.display="block")})})}});